  const [isConfetti, setIsConfetti] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  useEffect(() => {
    let arr = [];
    const premiumPromise = getPremium();
    premiumPromise
      .then((result) => {
        result.data.map((item) => {
          if (item.status) {
            arr.push(item);
            if (item.premiumname === "Half Love") {
              setPremiumStart(item);
            }
          }
        });
        setPremiums(arr);
        // window.scrollTo(0, 0);
      })
      .catch(() => {
        toast.error("No premium option to show");
      });
  }, []);

  const handleSubmitStart = async () => {
    const getUserToken = getUserByToken();
    getUserToken
      .then(() => {
        dispatch(
          setPremiumData({
            premium_id: premiumStart._id,
            paymentAmount:
              premiumStart.priceDiscount || premiumStart.priceOriginal,
            duration: premiumStart.durationByMonth,
            premiumname: premiumStart.premiumname,
          })
        );
        navigate("/checkout");
      })
      .catch(() => {
        toast.error("Please Login Before To Be A Member");
        // navigate("/login");
      });
  };
  const handelEffect = () => {
    setTimeout(() => {
      setIsConfetti(true);
    }, 600);
    setTimeout(() => {
      setIsConfetti(false);
    }, 4000);
  };

